import React, { Component } from "react";
import { Link } from "react-router-dom";
import logo from "../../img/logo.png";
import classnames from "classnames";
import "./style.css";

class Header extends Component {
  constructor() {
    super();
    this.state = {
      navbarShadow: false,
      prevScrollpos: window.pageYOffset,
      visible: true
    };
  }

  handleScrollVisibility() {
    const { prevScrollpos } = this.state;

    const currentScrollPos = window.pageYOffset;
    const visible = prevScrollpos > currentScrollPos;

    this.setState({
      prevScrollpos: currentScrollPos,
      visible
    });
  }

  handleScrollShadow() {
    const navStatus = document.documentElement.scrollTop > 30;
    this.setState({ navbarShadow: navStatus });
  }

  componentDidMount() {
    window.onscroll = () => [
      this.handleScrollShadow(),
      this.handleScrollVisibility()
    ];
  }

  // JSX stuff
  render() {
    return (
      <section id="nav-bar">
        <nav
          className={classnames(
            "navbar",
            { "navbar--shadow": this.state.navbarShadow },
            { "navbar--hidden": !this.state.visible },
            "navbar-expand-lg"
          )}
        >
          <div className="container">
            <div className="row col-12">
              <div className="col-3">
                <div className="text-center">
                  <i class="fas fa-home"></i>
                </div>
              </div>

              <div className="col-3">
                <div className="text-center">Oiiee</div>
              </div>

              <div className="col-3">
                <div className="text-center">Oiiee</div>
              </div>

              <div className="col-3">
                <div className="text-center">Oiiee</div>
              </div>
            </div>
          </div>
        </nav>
      </section>
    );
  }
}

export default Header;
