import React, { useState } from "react";
import { connect } from "react-redux";
import { inserirContato } from "../../../../../services";
import Recaptcha from "react-recaptcha";

const Formulario = () => {
  const [formData, setFormData] = useState({
    nome: "",
    email: "",
    mensagem: "",
    telefone: ""
  });

  const [recaptcha, setRecaptcha] = useState({ isVerified: false });

  const { nome, email, mensagem, telefone } = formData;
  const { isVerified } = recaptcha;

  // função que atualiza o state do recaptcha
  const verifyRecaptcha = response => {
    if (response) {
      setRecaptcha({ isVerified: true });
    }
  };

  // atualizando o value dos formularios
  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // lidando com o submit
  const handleSubmit = e => {
    e.preventDefault();

    // verificacao do recaptcha
    if (!isVerified) return alert("Você precisa provar que não é um robô!");

    inserirContato(formData);

    alert("Mensagem enviada com sucesso! Em breve entraremos em contato.");

    // resets
    window.grecaptcha.reset();
    setFormData({ nome: "", email: "", mensagem: "", telefone: "" });
  };

  return (
    <div>
      <form onSubmit={e => handleSubmit(e)}>
        <div className="form-group">
          <input
            required
            placeholder="Digite seu nome"
            name="nome"
            onChange={e => handleChange(e)}
            value={nome}
          />
        </div>
        <div className="form-group">
          <input
            required
            type="email"
            value={email}
            name="email"
            onChange={e => handleChange(e)}
            placeholder="Qual seu e-mail?"
          />
        </div>
        <div className="form-group">
          <input
            required
            placeholder="Digite seu telefone com DDD"
            name="telefone"
            onChange={e => handleChange(e)}
            value={telefone}
          />
        </div>
        <div className="form-group">
          <textarea
            required
            placeholder="Digite o que nos deseja enviar"
            value={mensagem}
            name="mensagem"
            onChange={e => handleChange(e)}
          />
          <small id="emailHelp" className="form-text text-white">
            Iremos responder o mais rápido possível!
          </small>
        </div>
        <div className="form-group ">
          <Recaptcha
            className="text-center"
            sitekey="6Lfcj64UAAAAAIRUuepRwDH6O3b9EyrDBEPd-aFH"
            render="explicit"
            verifyCallback={verifyRecaptcha}
          />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
};

export default connect(
  null,
  { inserirContato }
)(Formulario);
