import axios from "axios";
import { INSERIR_CONTATO, CONTATO_IS_LOADING } from "./types";

export const contatoIsLoanding = bool => async dispatch => {
  return {
    type: CONTATO_IS_LOADING,
    isLoading: bool
  };
};

export const inserirContato = formData => async dispatch => {
  // iniciando o state "loading" do formulário para true
  contatoIsLoanding(true);
  // criando objeto de configuração
  const config = {
    headers: {
      "Content-type": "application/json"
    }
  };

  // transformando JSON em string
  const body = JSON.stringify(formData);

  // conectando com API e inserindo os dados
  const response = await axios.post("/api/contato/inserir", body, config);

  // verificando se o formulario foi inserido corretamente
  if (!response.ok) {
    throw Error(response.statusText);
  }
};
