import React, { useState } from "react";
import { connect } from "react-redux";
import { inserirContato } from "../../../../../services";
import Recaptcha from "react-recaptcha";

const Formulario = () => {
  const [formData, setFormData] = useState({
    email: "",
    mensagem: "",
    telefone: "",
    isVerified: false
  });

  const { nome, email, mensagem, telefone, isVerified } = formData;

  // função que atualiza o state do recaptcha
  const verifyRecaptcha = response => {
    if (response) {
      setFormData({ ...formData, isVerified: true });
    }
  };

  // Função para alterar o State
  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    if (!isVerified) return alert("Você precisa provar que não é um robô!");

    inserirContato(formData);
    e.preventDefault();
  };

  return (
    <div>
      <form onSubmit={e => handleSubmit(e)}>
        <div className="form-group">
          <input
            type="text"
            name="nome"
            value={nome}
            onChange={e => handleChange(e)}
            placeholder="Digite o seu nome"
          />
        </div>
        <div className="form-group">
          <input
            required
            type="email"
            value={email}
            name="email"
            onChange={e => handleChange(e)}
            placeholder="Qual seu e-mail?"
          />
        </div>
        <div className="form-group">
          <input
            required
            placeholder="Digite seu telefone com DDD"
            name="telefone"
            onChange={e => handleChange(e)}
            value={telefone}
          />
        </div>
        <div className="form-group">
          <textarea
            required
            placeholder="Digite o que nos deseja enviar"
            value={mensagem}
            name="mensagem"
            onChange={e => handleChange(e)}
          />
          <small id="emailHelp" className="form-text text-white">
            Iremos responder o mais rápido possível!
          </small>
        </div>
        <div className="form-group  ">
          <Recaptcha
            sitekey="6Lfcj64UAAAAAIRUuepRwDH6O3b9EyrDBEPd-aFH"
            render="explicit"
            verifyCallback={verifyRecaptcha}
          />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
};

export default connect(
  null,
  { inserirContato }
)(Formulario);
